
public class Fourth_ArithemeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 	// expression = operands & operators
			// operands = values, variables, numbers, quantity
			// operators = + - * / %  (% is modulus, gives remainder)
		
		double chocolates = 10;
		chocolates++;									//++ or -- for adding or subtracting 1
		
		System.out.println(chocolates);
		
		chocolates=chocolates*2;
		System.out.println(chocolates);
		
		chocolates=chocolates%5;
		System.out.println(chocolates);
		
		chocolates = (int) chocolates/3;
		System.out.println(chocolates);
		
		
	}

}
